# find the largest product of 4 consecutive numbers within num
def largest_product(num):
    largest = 0
    num = str(num)
    start = 0
    end = 3
    while end < len(num):
        temp = int(num[start]) * int(num[start+1]) * int(num[start+2]) * int(num[end])
        if temp > largest:
            largest = temp
        start += 1
        end += 1

    return largest


